import{_ as t,o as e,c as s,V as a}from"./chunks/framework.bc1b501a.js";const S=JSON.parse('{"title":"Shell Scripts","description":"","frontmatter":{"next":{"text":"Install","link":"/install"}},"headers":[],"relativePath":"index.md","filePath":"index.md","lastUpdated":1687323507000}'),r={name:"index.md"},i=a('<h1 id="shell-scripts" tabindex="-1">Shell Scripts <a class="header-anchor" href="#shell-scripts" aria-label="Permalink to &quot;Shell Scripts&quot;">​</a></h1><p>Shell Scripts is a collection of POSIX shell and PowerShell utility scripts. These scripts are designed for my personal usage, but should be easily installable on any system.</p><h2 id="scripts" tabindex="-1">Scripts <a class="header-anchor" href="#scripts" aria-label="Permalink to &quot;Scripts&quot;">​</a></h2><p>The following table shows the avaible shell scripts. Each script is aviable in both POSIX shell and PowerShell versions if its functionality applies to both Unix and Windows systems.</p><table><thead><tr><th>Name</th><th>Description</th></tr></thead><tbody><tr><td>clear-cache</td><td>Frees up disk space by clearing caches of several package managers.</td></tr><tr><td>packup</td><td>Invokes upgrade commands to several installed package managers.</td></tr><tr><td>purge-snap</td><td>Removes all traces of the Snap package manager.</td></tr><tr><td>setup-tmate</td><td>Installs Tmate and creates a session suitable for CI pipelines.</td></tr><tr><td>trsync</td><td>Rsync for one time remote connections.</td></tr><tr><td>tscp</td><td>SCP for one time remote connections.</td></tr><tr><td>tssh</td><td>SSH for one time remote connections.</td></tr></tbody></table>',5),n=[i];function l(o,d,c,p,h,m){return e(),s("div",null,n)}const f=t(r,[["render",l]]);export{S as __pageData,f as default};
